class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        int zero_one_pair=0;
        int one=0;
        int zero=0;
        int cnt_1=0;
        vector<vector<int>>vis(n,vector<int>(m,0));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                cnt_1+=grid[i][j];
            }
        }
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j]) continue;
                int val1=grid[i][j];
                int val2=grid[i][m-1-j];
                int val3=grid[n-1-i][j];
                int val4=grid[n-1-i][m-1-j];
                vis[i][j]=1;
                vis[i][m-1-j]=1;
                vis[n-1-i][j]=1;
                vis[n-1-i][m-1-j]=1;
                set<pair<int,int>>st;
                st.insert({i,j});
                st.insert({n-1-i,m-1-j});
                st.insert({i,m-1-j});
                st.insert({n-1-i,j});
                int p=st.size();
                int sum1=val1+val2+val3+val4;
                if(p==4){
                ans+=min(4-sum1,sum1);
                if(sum1==1){
                   cnt_1-=1;
                }
                 if(sum1==2){
                   cnt_1+=2;
                }
                 if(sum1==3){
                   cnt_1+=1;
                }

                }
                else if(p==2){
                    int sum2=0;
                    for(auto ele:st) sum2+=grid[ele.first][ele.second];
                    if(sum2==1){
                        ans+=1;
                        zero_one_pair+=1;
                        cnt_1++;
                    }
                }
                else{
                    int sum3=sum1/4;
                    if(sum3==1){
                        one+=1;
                    }
                    else{
                        zero+=1;
                    }
                }
            }
        }
        cout<<zero_one_pair<<endl;
        cout<<cnt_1<<endl;
        cout<<ans<<endl;
        cout<<one<<endl;
        if((cnt_1%4)==0) return ans;
        if((cnt_1%4) ==2 ){
             if(zero_one_pair){
                return ans;
             }
             else{
                return ans+2;
             }
        }
        if((cnt_1 %4) ==1){
            if(one){
                return ans+1;
            }
            else if((zero_one_pair>=1) && (zero>=1) ){
                return ans+1;
            }
            else{
                return ans+3;
            }
        }
          if((cnt_1 %4) ==3){
            if(zero){
                return ans+1;
            }
            else if((zero_one_pair>=1) && (one>=1)){
                return ans+1;
            }
            else{
                return ans+3;
            }
        }
        return 0;
    }
};
