class Solution {
public:

  int minFlips(vector<vector<int>>&  arr) 
  {
        int n =  arr.size() ;
        int m =  arr[0].size();
        int cnt = 0;

        if (n % 2 == 1 && m % 2 == 1) 
        {
            if ( arr[n / 2][m / 2] == 1) 
            {
                cnt += 1;
            }
        }

        vector<int> v(3, 0);
        if (n % 2 == 1) 
        {
            int a = 0, b = m - 1;
            while (a < b) {
                int sm =  arr[n / 2][a] +  arr[n / 2][b];
                v[sm] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (m % 2 == 1) 
        {
            int a = 0, b = n - 1;
            while (a < b) 
            {
                int sm =  arr[a][m / 2] +  arr[b][m / 2];
                v[sm] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (v[2] % 2 == 1) 
        {
            if (v[1] > 0) {
                cnt += v[1];
            } else {
                cnt += 2;
            }
        } 
        else 
        {
            cnt += v[1];
        }

        for (int i = 0; i < n / 2; ++i) 
        {
            for (int j = 0; j < m / 2; ++j) 
            {
                int a = arr[i][j];
                int b = arr[n - 1 - i][j];
                int c = arr[i][m - 1 - j];
                int d = arr[n - 1 - i][m - 1 - j];

                int sum = a + b + c + d;

                if (sum != 0 && sum != 4) 
                {
                    cnt += min(sum, 4 - sum);
                }
            }
        }

        return cnt;
    }
};
