class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        tot = 0
        N = len(grid)
        M = len(grid[0])
        if M == 1 and N != 1:
            return self.minFlips(list(zip(*grid)))
        if N == 1:
            if M < 4:
                return sum(grid[0])
        a4 = 0
        for i in range(N//2):
            for j in range(M//2):
                c = grid[i][j] + grid[N-1-i][j] + grid[i][M-1-j] + grid[N-1-i][M-1-j]
                a4 += min(c, 4-c)
        a2 = 0
        if N%2 == 1:
            for j in range(M//2):
                if grid[N//2][j] != grid[N//2][M-1-j]:
                    a2 += 1
                tot += grid[N//2][j]
                tot += grid[N//2][M-1-j]
        if M%2 == 1:
            for i in range(N//2):
                if grid[i][M//2] != grid[N-1-i][M//2]:
                    a2 += 1
                tot += grid[i][M//2]
                tot += grid[N-1-i][M//2]
        a1 = 0
        if N%2 == 1 and M%2 == 1:
            a1 = grid[N//2][M//2]
            tot += grid[N//2][M//2]
        if tot % 4 == 0:
            if a2 % 2 == 0:
                return a4+a2
            return a4+a2+1
        elif tot % 4 == 2:
            if a2 == 0:
                return a4+2
            elif a2 % 2 == 0:
                return a4+a2
            return a4+a2+1
        if a2 == 0 and tot % 4 == 3 and a1 == 1:
            return a4+3
        elif a2 % 2 == 0:
            return a4+a2+1
        return a4+a2
