class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
    int n = grid.size();
    int m = grid[0].size();
    int flips = 0;
    for(int i=0;i<n/2;i++){
        for(int j=0;j<m/2;j++){
            // if((j==m-1-j) && i==n-1-i){
            //     if(grid[i][j]) flips++;
            //     continue;
            // }
            // if(j==(m-1-j)){
            //     continue;
            // }
            // if(i==(n-1-i)){
            //     continue;
            // }
            // cout<<i<<" "<<j<<endl;
            int cnt1=0,cnt0=0;
            if(grid[i][j]==1) cnt1++;
            else cnt0++;
            if(grid[i][m-1-j]) cnt1++;
            else cnt0++;
            if(grid[n-1-i][j]) cnt1++;
            else cnt0++;
            if(grid[n-1-i][m-1-j]) cnt1++;
            else cnt0++;
            if(i==(n-1-i)) cnt1/=2,cnt0/=2;
            if(cnt1<cnt0){
                flips+=cnt1;
                grid[i][j]=0;grid[i][m-1-j]=0;
                grid[n-1-i][j]=0;
                grid[n-1-i][m-1-j]=0;
            }
            else{
                flips+=cnt0;
                grid[i][j]=1;grid[i][m-1-j]=1;
                grid[n-1-i][j]=1;
                grid[n-1-i][m-1-j]=1;
            }
        }
    }
    if((n & 1) && (m & 1)){
        if(grid[n/2][m/2]) flips++;
    }
    int cnt11=0,cnt00=0,cnt01=0;
    if(n & 1){
        for(int j=0;j<m/2;j++){
            if((grid[n/2][j]+grid[n/2][m-1-j])==1) cnt01++;
            if((grid[n/2][j]+grid[n/2][m-1-j])==0) cnt00++;
            if((grid[n/2][j]+grid[n/2][m-1-j])==2) cnt11++;
        }
    }
    if(m & 1){
        for(int i=0;i<n/2;i++){
            if((grid[i][m/2]+grid[n-1-i][m/2])==1) cnt01++;
            if((grid[i][m/2]+grid[n-1-i][m/2])==0) cnt00++;
            if((grid[i][m/2]+grid[n-1-i][m/2])==2) cnt11++;
        }
    }
    if(cnt01>0 || (cnt11%2==0)) return flips+cnt01;
    if((cnt11%2)) return flips+2;
    return flips;

    }
};
