class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                vector<int> elements = {
                    grid[i][j],
                    grid[n - i - 1][j],
                    grid[i][m - j - 1],
                    grid[n - i - 1][m - j - 1]
                };

                int ones = count(elements.begin(), elements.end(), 1);
                ans += min(ones, 4 - ones);
            }
        }
        int possible = 0;
        int count = 0;
        if (n % 2 != 0) {
            int mid_row = n / 2;
            for (int j = 0; j < m / 2; ++j) {
                if (grid[mid_row][j] != grid[mid_row][m - j - 1]) {
                    ans += 1;
                    possible += 1;
                } else {
                    if (grid[mid_row][j] == 1) {
                        count += 1;
                    }
                }
            }
        }
        if (m % 2 != 0) {
            int mid_col = m / 2;
            for (int i = 0; i < n / 2; ++i) {
                if (grid[i][mid_col] != grid[n - i - 1][mid_col]) {
                    ans += 1;
                    possible += 1;
                } else {
                    if (grid[i][mid_col] == 1) {
                        count += 1;
                    }
                }
            }
        }
        if (n % 2 != 0 && m % 2 != 0 && grid[n / 2][m / 2] == 1) {
            ans += 1;
        }

        if (possible == 0 && count % 2 != 0) {
            ans += 2;
        }

        return ans;
    }
};
