class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        ios::sync_with_stdio(false);
        cin.tie(nullptr);
        int n = grid.size();
        int m = grid[0].size();
        int flips = 0;
        for(int i = 0; i<n/2; i++){
            for(int j = 0; j<m/2; j++){
                int count0 = 0;
                int count1 = 0;
                if(grid[i][j] == 0) count0++;
                else count1++;
                if(grid[n-i-1][j] == 0) count0++;
                else count1++;
                if(grid[i][m-j-1] == 0) count0++;
                else count1++;
                if(grid[n-i-1][m-j-1] == 0) count0++;
                else count1++;
                /////////////////////////
                flips += min(count0,count1);
            }
        }
        int count = 0;
        int flip1 = 0;
        int flip2 = 0;
        if(n%2 == 1){
            //// i = n/2+1;
            // int flip1 = 0;
            for(int j = 0; j<m/2; j++){
                int i = n/2;
                if(grid[i][j] != grid[i][m-j-1]) flip1++;
                else if(grid[i][j] == grid[i][m-j-1] && grid[i][j] == 1) count+=2;
            }
            flips += flip1;
        }
        if(m%2 == 1){
            for(int i = 0; i<n/2; i++){
                int j = m/2;
                if(grid[i][j] != grid[n-i-1][j]) flip2++;
                else if(grid[i][j] == grid[n-i-1][j] && grid[i][j] == 1) count+=2;
            }
            flips+= flip2;
        }
        if(count%4 != 0){
                int req = 4 - count%4;
                if(req/2 <= (flip1+flip2)) count = 0;
            }

        if(n%2 != 0 && m%2 != 0){
            if(grid[n/2][m/2] == 1) flips++;
        }
        flips += count%4;
        return flips;
    }
};
