class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans += 1;
            }
        }

        vector<int> change(3, 0);
        if (n % 2 == 1) {
            int i = 0, j = m - 1;
            while (i < j) {
                int x = grid[n / 2][i] + grid[n / 2][j];
                change[x] += 1;
                i += 1;
                j -= 1;
            }
        }

        if (m % 2 == 1) {
            int i=0, j=n-1;
            while (i < j) {
                int x = grid[i][m / 2] + grid[j][m / 2];
                change[x] += 1;
                i += 1;
                j -= 1;
            }
        }

        if (change[2] % 2 == 1) {
            if (change[1] > 0) {
                ans += change[1];
            } else {
                ans += 2;
            }
        } else {
            ans += change[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int x = a + b + c + d;

                if (x != 0 && x != 4) {
                    ans += min(x, 4 - x);
                }
            }
        }

        return ans;
    }
};
