class Solution
{
public:
    int minFlips(vector<vector<int>>& grid)
    {
        int n = grid.size();
        int m = grid[0].size();

        int n1 = n / 2 - 1;
        int m1 = m / 2 - 1;

        int ans = 0;
        for (int i = 0; i <= n1; ++i)
        {
            for (int j = 0; j <= m1; ++j)
            {
                int c = 0;

                if (grid[i][j] == 1) c++;
                if (grid[i][m - 1 - j] == 1) c++;
                if (grid[n - 1 - i][j] == 1) c++;
                if (grid[n - 1 - i][m - 1 - j] == 1) c++;

                //cout << i << " " << j << " " << c << endl;

                if (c == 2) ans += 2;
                else if (c == 1 || c == 3)
                    ans++;
            }
        }
        //cout << ans << endl;

        int c00 = 0, c01 = 0, c11 = 0;
        if (n % 2 == 1 && m % 2 == 1)
        {
            if (grid[n / 2][m / 2] == 1) ans++;
        }

        if (n % 2 == 1)
        {
            int l = 0, r = m - 1;
            while (l < r)
            {
                if (grid[n / 2][l] != grid[n / 2][r]) c01++;
                else
                {
                    if (grid[n / 2][l] == 0) c00++;
                    else
                        c11++;
                }
                l++, r--;
            }

        }
        if (m % 2 == 1)
        {
            int l = 0, r = n - 1;
            while (l < r)
            {
                if (grid[l][m / 2] != grid[r][m / 2]) c01++;
                else
                {
                    if (grid[l][m / 2] == 0) c00++;
                    else
                        c11++;
                }
                l++, r--;
            }

        }

        //cout <<  " f " << c00 << " " << c01 << " " << c11 << endl;

        if (c11 % 2 == 0) ans += c01;
        else
        {
            if (c01 >= 1)
            {
                ans += c01;
            }
            else
            {
                ans += 2;
            }
        }

        return ans;
    }
};
