class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        def cal_row(one_size, pair_size):
            for j in range(h_col):
                fj = -(j + 1)
                if grid[v_row][j] == grid[v_row][fj]:
                    if grid[v_row][j] == 1:
                        pair_size += 1
                else:
                    one_size += 1
            return one_size, pair_size

        def cal_col(one_size, pair_size):
            for i in range(v_row):
                fi = -(i + 1)
                if grid[i][h_col] == grid[fi][h_col]:
                    if grid[i][h_col] == 1:
                        pair_size += 1
                else:
                    one_size += 1
            return one_size, pair_size

        h_col = int(len(grid[0]) / 2)
        v_row = int(len(grid) / 2)

        flip_count = 0
        for i in range(v_row):
            for j in range(h_col):
                fi, fj = -(i + 1), -(j + 1)
                one_size = grid[i][j] + grid[i][fj] + grid[fi][j] + grid[fi][fj]
                if one_size == 1 or one_size == 3:
                    flip_count += 1
                elif one_size == 2:
                    flip_count += 2

        if len(grid[0]) % 2 != 0 and len(grid) % 2 != 0:
            if grid[v_row][h_col] == 1:
                flip_count += 1
            one_size, pair_size = cal_row(*cal_col(0, 0))
            if pair_size % 2 != 0 and one_size == 0:
                flip_count += 2
            flip_count += one_size
        elif len(grid[0]) % 2 != 0:  # 奇数列 -> 中间列
            one_size, pair_size = cal_col(0, 0)
            if pair_size % 2 != 0 and one_size == 0:
                flip_count += 2
            flip_count += one_size
        elif len(grid) % 2 != 0:  # 奇数行 -> 中间行
            one_size, pair_size = cal_row(0, 0)
            if pair_size % 2 != 0 and one_size == 0:
                flip_count += 2
            flip_count += one_size
        return flip_count
