class Solution {
public:
    int rec(int ind,int rem,vector<pair<int,int>>&cnt,vector<vector<int>>&dp){
        if(ind==cnt.size() && rem==0) return 0;
        if(ind==cnt.size() && rem!=0) return 1e9;
        if(dp[ind][rem]!=-1) return dp[ind][rem];

        int tempans=cnt[ind].first+rec(ind+1,rem,cnt,dp);
        int ans=cnt[ind].second+rec(ind+1,(rem%4+2)%4,cnt,dp);


        return dp[ind][rem]=min(ans,tempans);
    }
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();

        vector<pair<int,int>>cnt,cnt2;
        for(int j=0;j<n/2;j++){
            for(int k=0;k<m/2;k++){
                int cnt1=0;
                int cnt0=0;
               if(grid[j][k]==1) cnt1++;
               else cnt0++;

               if(grid[n-1-j][k]==1) cnt1++;
               else cnt0++;

               if(grid[j][m-1-k]==1) cnt1++;
               else cnt0++;

               if(grid[n-1-j][m-1-k]==1) cnt1++;
               else cnt0++;

              cnt.push_back({cnt1,cnt0});
            }
        }
        if(m%2==1){

          for(int j=0;j<n/2;j++){
                int cnt1=0;
                int cnt0=0;

                if(grid[j][m/2]==1) cnt1++;
                else cnt0++;

                if(grid[n-1-j][m/2]==1) cnt1++;
                else cnt0++;

                cnt2.push_back({cnt1,cnt0});
            }


        }
        if(n%2==1){

            for(int j=0;j<m/2;j++){
                int cnt1=0;
                int cnt0=0;

                if(grid[n/2][j]==1) cnt1++;
                else cnt0++;

                if(grid[n/2][m-1-j]==1) cnt1++;
                else cnt0++;

                cnt2.push_back({cnt1,cnt0});
            }

        }
        for(int j=0;j<cnt2.size();j++){
            cout<<cnt2[j].first<<" "<<cnt2[j].second<<endl;
        }
        vector<vector<int>>dp(cnt2.size(),vector<int>(4,-1));
        int ans=0;
        if(n%2==1 && m%2==1 && grid[n/2][m/2]==1){
            for(int j=0;j<cnt.size();j++){
                ans+=min(cnt[j].first,cnt[j].second);
            }

            int tempans=1+rec(0,0,cnt2,dp);
            ans+=tempans;

        }
        else{

            for(int j=0;j<cnt.size();j++){
                ans+=min(cnt[j].first,cnt[j].second);
            }

            ans+=rec(0,0,cnt2,dp);
        }

        return ans;
    }
};
