class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int total = 0;    
        vector<int> matcher(3, 0);

        if ((n % 2) && (m % 2)) {
            if (grid[n / 2][m / 2]) {
                total += 1;
            }
        }

        if (n % 2) {
            int first = 0, second = m - 1;
            while (first < second) {
                int sum_yet = grid[n / 2][first] + grid[n / 2][second];
                matcher[sum_yet] += 1;
                first += 1;
                second -= 1;
            }
        }

        if (m % 2) {
            int elem1 = 0, elem2 = n - 1;
            while (elem1 < elem2) {
                int sum_yet = grid[elem1][m / 2] + grid[elem2][m / 2];
                matcher[sum_yet] += 1;
                elem1 += 1;
                elem2 -= 1;
            }
        }

        if (matcher[2] % 2) {
            if (matcher[1] > 0) {
                total += matcher[1];
            } else {
                total += 2;
            }
        } else {
            total += matcher[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {

                int sum_yet = grid[i][j] + grid[n - 1 - i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][m - 1 - j];;

                if (sum_yet != 0 && sum_yet != 4) {
                    total += min(sum_yet, 4 - sum_yet);
                }
            }
        }

        return total;
    }
};
