class Solution {
public:
    int solve(vector<vector<int>>&grid,vector<vector<int>>&vis,int i,int j,int n,int m,int& p,int& pp){
        set<pair<int,int>>s;
        s.insert({i,j});
        s.insert({i,m-j});
        s.insert({n-i,j});
        s.insert({n-i,m-j});
        int o=0,z=0;
        for(auto x:s){
            if(grid[x.first][x.second]==0) z++;
            else o++;
            vis[x.first][x.second]=1;
        }
        if(o==2 && z==0){
                p=!p;
        }
        if(s.size()==2 && o==1) pp=1;
        if(s.size()==1 && o==1) return 1;
        return min(o,z);

    }
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size()-1,m=grid[0].size()-1,ans=0,p=0,pp=0;
        vector<vector<int>>vis(n+1,vector<int>(m+1,0));
        // for(int i=0;i<=n;i++){
        //     for(int j=0;j<=m;j++){
        //         if(grid[i][j]==1) one++;
        //     }
        // }
        for(int i=0;i<=n;i++){
            for(int j=0;j<=m;j++){
                if(!vis[i][j]) ans+=solve(grid,vis,i,j,n,m,p,pp);
            }
        }
        cout<<p<<endl;
        return ans+2*(p && !pp);

    }
};
