class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int row = g.size();
        int col = g[0].size();
        int ans = 0;
        vector<vector<int>> log(row, vector<int>(col, 0));
        for (int i = 0; i < row / 2; ++i) {
            for (int j = 0; j < col / 2; ++j) {
                int sum = g[i][j] + g[i][col - 1 - j] + g[row - 1 - i][j] + g[row - 1 - i][col - 1 - j];
                ans += min(sum, 4 - sum);
                log[i][j] = 1;
                log[i][col - 1 - j] = 1;
                log[row - 1 - i][j] = 1;
                log[row - 1 - i][col - 1 - j] = 1;
            }
        }
        vector<int> dp1(4, 0);
        vector<int> dp2(4, 0);
        dp1[2] = INT_MAX / 10;
        dp2[2] = INT_MAX / 10;
        {
            if (row & 1) {
                for (int i = 0; i < col / 2; ++i) {
                    vector<int> tmp(4, 0);
                    if (g[row / 2][i] == g[row / 2][col - 1 - i]) {
                        if (g[row / 2][i] == 1) {
                            tmp[0] = min(dp1[2], dp1[0] + 2);
                            tmp[2] = min(dp1[0], dp1[2] + 2);
                        } else {
                            tmp[0] = min(dp1[2] + 2, dp1[0]);
                            tmp[2] = min(dp1[0] + 2, dp1[2]);
                        }
                    } else {
                        tmp[0] = min(dp1[0] + 1, dp1[2] + 1);
                        tmp[2] = min(dp1[0] + 1, dp1[2] + 1);
                    }
                    swap(dp1, tmp);
                }
            }
            if (col & 1) {
                for (int i = 0; i < row / 2; ++i) {
                    vector<int> tmp(4, 0);
                    if (g[i][col / 2] == g[row - 1 - i][col / 2]) {
                        if (g[i][col / 2] == 1) {
                           tmp[0] = min(dp2[2], dp2[0] + 2);
                            tmp[2] = min(dp2[0], dp2[2] + 2);
                        } else {
                            tmp[0] = min(dp2[2] + 2, dp2[0]);
                            tmp[2] = min(dp2[0] + 2, dp2[2]);
                        }
                    } else {
                        tmp[0] = min(dp2[0] + 1, dp2[2] + 1);
                        tmp[2] = min(dp2[0] + 1, dp2[2] + 1);
                    }
                    swap(dp2, tmp);

                }
            }
        }
        if ((col & 1) && (row & 1) && g[row / 2][col / 2]) {
            ++ans;
        }
        // cout << min(dp1[0] + dp2[0], dp1[2] + dp2[2]) << endl;
        return ans + min(dp1[0] + dp2[0], dp1[2] + dp2[2]);
    }
};
