class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),res=0;

        for(int i=0;i<n/2;++i) {
            for(int j=0;j<m/2;++j) {
                vector<int> elems={grid[i][j],grid[n-i-1][j],grid[i][m-j-1],grid[n-i-1][m-j-1]};
                int ones=count(elems.begin(),elems.end(),1);
                res+=min(ones,4-ones);
            }
        }

        int poss=0,cnt=0;
        if(n%2!=0) {
            for(int j=0;j<m/2;++j) {
                if(grid[n/2][j]!=grid[n/2][m-j-1]) {
                    ++res;
                    ++poss;
                } else if(grid[n/2][j]==1) {
                    ++cnt;
                }
            }
        }

        if(m%2!=0) {
            for(int i=0;i<n/2;++i) {
                if(grid[i][m/2]!=grid[n-i-1][m/2]) {
                    ++res;
                    ++poss;
                } else if(grid[i][m/2]==1) {
                    ++cnt;
                }
            }
        }

        if(n%2!=0&&m%2!=0&&grid[n/2][m/2]==1) {
            ++res;
        }

        if(poss==0&&cnt%2!=0) {
            res+=2;
        }

        return res;
    }
};
