class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        int res = 0, c01 = 0, c11 = 0;
        for(int i = 0; 2 * i + 1 <= n; i++) {
            for(int j = 0; 2 * j + 1 <= m; j++) {
                int c1 = g[i][j] + g[n - i - 1][j] + g[i][m - j - 1] + g[n - i - 1][m - j - 1];
                if(2 * i + 1 == n and 2 * j + 1 == m) {
                    res += (c1 / 4);
                    //cout << "f" << endl;
                }
                else if(2 * i + 1 == n or 2 * j + 1 == m) {
                    c1 /= 2;
                    if(c1 == 1) c01++;
                    else if(c1 == 2) c11++;
                }
                else {
                    res += min(4 - c1, c1);
                }
            }
        }
        //cout << c01 << " " << c11 << endl;
        res += c01;
        if(c11 % 2 and !c01) res += 2;
        return res;

    }
};
