class Solution {
public:
    int helper(vector<int>& nums1, vector<int>& nums2){
        int size1=nums1.size();
        int size2=nums2.size();

        int ans=0;

        // if((size/2) == 1){
        //     ans+= (nums[0]+nums[size-1]);
        //     return ans;
        // }


        int oneone=0;
        int oneZero=0;

        for(int i=0; i<size1/2; i++){
            if(nums1[i] == 1 && nums1[size1-1-i] == 1){
                oneone++;
            }
            else if(nums1[i]+nums1[size1-1-i] == 1){
                oneZero++;
            }
        }

        for(int i=0; i<size2/2; i++){
            if(nums2[i] == 1 && nums2[size2-1-i] == 1){
                oneone++;
            }
            else if(nums2[i]+nums2[size2-1-i] == 1){
                oneZero++;
            }
        }

        if(oneone%2 == 1 && oneZero==0){
            ans+=2;
            return ans;
        }

        ans+=oneZero;

        return ans;

    }

    int minFlips(vector<vector<int>>& grid) {
        int numRows=grid.size();
        int numCols=grid[0].size();
        int ans=0;
        for(int i=0; i<numRows/2; i++){
            for(int j=0; j<numCols/2; j++){
                int first=grid[i][j];
                int second= grid[numRows-i-1][j];
                int third= grid[i][numCols-j-1];
                int fourth=grid[numRows-i-1][numCols-j-1];

                int sum=first+second+third+fourth;

                if(sum==2){
                    ans+=2;
                }
                else if(sum==1 || sum==3){
                    ans++;
                }
            }
        }

        bool flag1=false;
        vector<int> nums1;
        if(numRows%2==1){
            flag1=true;

            for(int i=0; i<numCols; i++){
                nums1.push_back(grid[numRows/2][i]);
            }
            // ans+= helper(nums);
        }
        bool flag2 =false;
        vector<int> nums2;
        if(numCols%2 == 1){
            // cout<<"HI";
            flag2=true;

            for(int i=0; i<numRows; i++){
                nums2.push_back(grid[i][numCols/2]);
            }

        }

        ans+=helper(nums1,nums2);

        if(flag1 && flag2 && grid[numRows/2][numCols/2] == 1){
            ans++;
        }

        return ans;




    }
};
