class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n = v.size(), m = v[0].size();
        int ct=0;
        set<int> spare;
        int num = 0;
        vector<vector<bool>> vis(n, vector<bool>(m, false));
        for (int i=0; i<n; i++)
        {
            for (int j=0; j<m; j++)
            {
                if (vis[i][j])
                    continue;

                int val = 0;
                set<pair<int,int>> st;
                st.insert({i,j});
                st.insert({i,m-1-j});
                st.insert({n-1-i,j});
                st.insert({n-1-i,m-1-j});
                for (auto pr:st)
                {
                    val += v[pr.first][pr.second];
                    vis[pr.first][pr.second] = true;
                }
                int len = st.size();

                v[i][j] = 0;
                v[i][m-1-j] = 0;
                v[n-i-1][j] = 0;
                v[n-1-i][m-1-j] = 0;
                ct += min(val, len-val);
                if (len==2)
                {
                    if (val==1)
                    {
                        spare.insert(len);
                    }
                    else if (val==2)
                    {
                        num += len;
                    }
                }
                if (len==1)
                {
                    if (val==1)
                        num++;
                }
            }
        }
        int tem = num%4;
        for (auto val:spare)
        {
            tem = min(tem, (num+val)%4);
        }
        ct+=tem;
        // num = num%4;
        // if (num==1)
        // {
        //     if (spare.find(3)==spare.end())
        //         ct++;
        // }
        // if (num==2)
        // {
        //     if (spare.find(2)==spare.end())
        //         ct+=2;
        // }
        // if (num==3)
        // {
        //     if (spare.find(1)==spare.end())
        //         ct+=3;
        // }
        return ct;
    }
};
