class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();
        int flips = 0;
        int arr[3] = {0};
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int tot = grid[rows / 2][left] + grid[rows / 2][right];
                arr[tot] += 1;
                left += 1;
                right -= 1;
            }
        }
        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int tot = grid[top][cols / 2] + grid[bottom][cols / 2];
                arr[tot] += 1;
                top += 1;
                bottom -= 1;
            }
        }
        if (arr[2] % 2 == 1) {
            if (arr[1] > 0) {
                flips += arr[1];
            } else {
                flips += 2; 
            }
        } else {
            flips += arr[1];
        };
         if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                flips += 1;
            }
        }
        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int topLeft = grid[i][j];
                int bottomLeft = grid[rows - 1 - i][j];
                int topRight = grid[i][cols - 1 - j];
                int bottomRight = grid[rows - 1 - i][cols - 1 - j];

                int tot = topLeft + bottomLeft + topRight + bottomRight;

                if (tot != 0 && tot != 4) {
                    flips += min(tot, 4 - tot);
                }
            }
        }

        return flips;
    }
};
