class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int count=0;
        int a=0,b=0,c=0 ,res=0;
        for(int i=0;i<n/2;i++)
        {

            for(int j=0;j<m/2;j++)
            {
                if(grid[i][j]==grid[i][m-j-1] && grid[i][j]==grid[n-i-1][j] && grid[i][j]==grid[n-i-1][m-j-1])
                {
                   if(grid[i][j]==1)
                       count+=4;
                }
                else
                {
                    int x = grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1];

                    if(x==1)
                    {
                       a++;
                         res += 1;
                    }
                    else if(x==2)
                    {
                        b++;
                         res += 2;
                    }
                    else if(x==3)
                    {
                        c++;
                         res += 1;
                    }


                }
            }

        }
        int ex=0,c2=0,c1=0;
        if(n%2==1)
        {
            for(int i=0;i<m/2;i++)
            {
                if(grid[n/2][i]==grid[n/2][m-i-1])
                {
                    if(grid[n/2][i]==1)
                    c2++;
                    else
                        c1++;
                }
                else
                {
                    ex++;
                }
            }
        }

        if(m%2==1)
        {
            for(int i=0;i<n/2;i++)
            {
                if(grid[i][m/2]==grid[n-i-1][m/2])
                {
                    if(grid[i][m/2]==1)
                        c2++;
                    else
                        c1++;
                }
                else
                    ex++;
            }
        }


        if(m%2==1 && n%2==1)
        {
            if(grid[n/2][m/2]==1)
                res++;
        }

        if(c2%2==1)
        {
            if(ex>0)
                res+=ex;
            else
                res+=2;
        }
        else
            res+=ex;
//         a
//             tem++
//             tem+=2 c+=3

//         b
//             tem++ c+=3
//             tem+=2 


        return res;
    }
};
