/**
 * @param {number[][]} grid
 * @return {number}
 */
var minFlips = function(grid) {
    const row = grid.length;
    const col = grid[0].length;
    let ones = null;
    let twodiff = 0;
    let twosame0 = 0;
    let twosame1 = 0;
    let res = 0;
    for (let i = 0; i < row; ++i) {
        for (let j = 0; j < col; ++j) {
            if (i !== row - 1 - i) {
                if (j !== col - 1 - j) {
                    let curone = grid[i][j] + grid[row - 1 - i][j] + grid[i][col - 1 - j] + grid[row - 1 - i][col - 1 - j];
                    res += Math.min(curone, 4 - curone);
                } else {
                    if (grid[i][j] === grid[row - 1 - i][j]) {
                        if (grid[i][j]) {
                            twosame1++;
                        } else {
                            twosame0++;
                        }
                    } else {
                        twodiff++;
                    }
                }
            } else {
                if (j !== col - 1 - j) {
                    if (grid[i][j] === grid[i][col - 1 - j]) {
                        if (grid[i][j]) {
                            twosame1++;
                        } else {
                            twosame0++;
                        }
                    } else {
                        twodiff++;
                    }
                } else {
                    ones = [i, j];
                }
            }
        }
    }
    res /= 4;
    twodiff /= 2;
    twosame0 /= 2;
    twosame1 /= 2;
    if (ones) {
        if (grid[ones[0]][ones[1]]) {
            ++res;
        }
    }
    res += twodiff;
    if (twosame1 & 1) {
        if (!twodiff) {
            res += 2;
        }
    }
    return res;
};
