class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int ans=0;
        int n = a.size(),m=a[0].size();
        // i,j == n-i,m-j == n-i,j == i,m-j;
        for(int i=1;i<=n/2;i++) 
        {
            for(int j=1;j<=m/2;j++)
            {
                int sum=0;
                sum+=a[i-1][j-1];
                sum+=a[n-i][j-1];
                sum+=a[i-1][m-j];
                sum+=a[n-i][m-j];
                if(sum>2)
                    ans+=4-sum;
                else
                    ans+=sum;
            }
        }
        cout<<ans<<" ";
        if(n%2 and m%2==0)
        {
            int c1=0,c2=0;
            for(int i=1;i<=m/2;i++)
            {
                if(a[n/2][m-i]!=a[n/2][i-1])
                    c1++;
                else if(a[n/2][m-i]==1)
                    c2++;
            }
            if(c2%2==0)
            {
                ans+=c1;
            }
            else
            {
                if(c1>=1)
                {
                    ans+=c1;
                }
                else
                {
                    ans+=2;
                }
            }
        }
        else if(m%2 and n%2==0)
        {
            int c1=0,c2=0;
            for(int i=1;i<=n/2;i++)
            {
                if(a[n-i][m/2]!=a[i-1][m/2])
                    c1++;
                else if(a[i-1][m/2]==1)
                    c2++;
            }
            if(c2%2==0)
            {
                ans+=c1;
            }
            else
            {
                if(c1>=1)
                {
                    ans+=c1;
                }
                else
                {
                    ans+=2;
                }
            }
        }
        else if(n%2 and m%2)
        {
            int c1=0,c2=0;
            for(int i=1;i<=m/2;i++)
            {
                if(a[n/2][m-i]!=a[n/2][i-1])
                    c1++;
                else if(a[n/2][m-i]==1)
                    c2++;
            }
            int c3=0,c4=0;
            for(int i=1;i<=n/2;i++)
            {
                if(a[n-i][m/2]!=a[i-1][m/2])
                    c3++;
                else if(a[i-1][m/2]==1)
                    c4++;
            }
            if(c2%2 == c4%2)
            {
                ans+=c1+c3;   
            }
            else
            {
                if(c1>=1 || c3>=1)
                {
                    ans+=c1+c3;
                }
                else
                {
                    ans+=2;
                }
            }
            if(a[n/2][m/2])
                ans++;    
        }
        cout<<ans<<endl;
        return ans;
    }
};
