class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n = len(grid)
        m = len(grid[0])       
        res = 0
        for i in range(n // 2):
            for j in range(m // 2):
                xx = [
                    grid[i][j],
                    grid[n - i - 1][j],
                    grid[i][m - j - 1],
                    grid[n - i - 1][m - j - 1]
                ]               
                ones = sum(xx)               
                res += min(ones, 4 - ones)       
        pp = 0
        yy = 0
        if n % 2 != 0:
            for j in range(m // 2):
                if grid[n // 2][j] != grid[n // 2][m - j - 1]:
                    res += 1
                    pp += 1
                else:
                    if grid[n // 2][j] == 1:
                        yy += 1      
        if m % 2 != 0:
            for i in range(n // 2):
                if grid[i][m // 2] != grid[n - i - 1][m // 2]:
                    res += 1
                    pp += 1
                else:
                    if grid[i][m // 2] == 1:
                        yy += 1       
        if n % 2 != 0 and m % 2 != 0 and grid[n // 2][m // 2] == 1:
            res += 1       
        if pp == 0 and yy % 2 != 0:
            res += 2
        return res
