class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
    //   int sum=0;
        int n=grid.size(),m=grid[0].size();
    //   for(auto it:grid){
    //     for(auto it1:it){
    //         sum+=it1;
    //     }
    //   }
    //   int ans1=0,ans2=0;
    //     for(auto it:grid){
    //         int n=it.size();
    //         for(int i=0;i<n/2;i++){
    //             if(it[i]!=it[n-1-i]) ans1++;
    //         }
    //     }
    //     for(int i=0;i<n/2;i++){
    //         for(int j=0;j<m;j++){
    //             if(grid[i][j]!=grid[n-1-i][j]) ans2++;
    //         }
    //     }
    //     int sum1=sum%4;
    //     if(sum1){
    //         sum1=4-sum1;
    //     }
        int ans=0,c=0,x=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int sum=0;
                if(grid[i][j]==1) sum++; 
                if(grid[n-i-1][j]==1) sum++; 
                if(grid[i][m-1-j]==1) sum++; 
                if(grid[n-i-1][m-1-j]==1) sum++; 
                ans+=min(sum,4-sum);
            }
        }

        if (n % 2 != 0) {
            int i=n/2;
            for (int j = 0; j < m / 2; ++j) {
                if (grid[i][j] != grid[i][m-j-1]) {
                    ans++;
                    x++;
                } else {
                    if (grid[i][j] == 1) {
                        c++;
                    }
                }
            }
        }
        if (m % 2 != 0) {
            int j=m/2;
            for (int i = 0; i < n / 2; ++i) {
                if (grid[i][j] != grid[n-i-1][j]) {
                    ans++;
                    x++;
                } else {
                    if (grid[i][j] == 1) {
                        c++;
                    }
                }
            }
        }

        if (n%2!= 0 && m%2!=0 && grid[n/2][m/2] == 1) {
            ans++;
        }

        if (x == 0 && c % 2 != 0) {
            ans += 2;
        }
        return ans;
    }
};
