class Solution {
public:
    int minFlips(vector<vector<int>>& v) 
    {
        int n=v.size(),m=v[0].size();
        int cnt=0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int cnt1=0,cnt0=0;
                if(v[i][j])
                    ++cnt1;
                else
                    ++cnt0;
                if(v[i][m-1-j])
                    ++cnt1;
                else
                    ++cnt0;
                if(v[n-i-1][j])
                    ++cnt1;
                else
                    ++cnt0;
                if(v[n-1-i][m-j-1])
                    ++cnt1;
                else
                    ++cnt0;
                cnt+=min(cnt1,cnt0);
            }
        }

        int cnt11=0,cnt10=0,cnt00=0;
        if (n % 2) {
            int mid = n / 2;
            for (int j = 0; j < m / 2; j++) {
                int s=v[mid][j] + v[mid][m - 1 - j];
                if(s==1)
                    ++cnt10;
                else if(s==2)
                    ++cnt11;
                else
                    ++cnt00;
            }
        }
        if (m % 2) {
            int mid = m / 2;
            for (int i = 0; i < n / 2; i++) {
                int s= v[i][mid] + v[n - 1 - i][mid];
                if(s==1)
                    ++cnt10;
                else if(s==2)
                    ++cnt11;
                else
                    ++cnt00;
            }
        }
        int tot = 0;
        cnt+=cnt10;
        //cout<<cnt<<" "<<cnt11<<" "<<cnt10<<endl;
        if(cnt11%2)
        {
            if(cnt10==0)
                cnt+=2;
        }
        if(n%2 && m%2 && v[n/2][m/2])
            ++cnt;
        //cout<<cnt<<endl;
        return cnt;
    }
};
