#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    int minFlips(vector<vector<int>>& buguGrid) {
        int buguN = buguGrid.size(), buguM = buguGrid[0].size();
        int buguAns = 0;

        if (buguN % 2 == 1 && buguM % 2 == 1) {
            if (buguGrid[buguN / 2][buguM / 2] == 1) {
                buguAns += 1;
            }
        }

        vector<int> buguShift(3, 0);
        if (buguN % 2 == 1) {
            int buguA = 0, buguB = buguM - 1;
            while (buguA < buguB) {
                int buguSm = buguGrid[buguN / 2][buguA] + buguGrid[buguN / 2][buguB];
                buguShift[buguSm] += 1;
                buguA += 1;
                buguB -= 1;
            }
        }

        if (buguM % 2 == 1) {
            int buguA = 0, buguB = buguN - 1;
            while (buguA < buguB) {
                int buguSm = buguGrid[buguA][buguM / 2] + buguGrid[buguB][buguM / 2];
                buguShift[buguSm] += 1;
                buguA += 1;
                buguB -= 1;
            }
        }

        if (buguShift[2] % 2 == 1) {
            if (buguShift[1] > 0) {
                buguAns += buguShift[1];
            } else {
                buguAns += 2;
            }
        } else {
            buguAns += buguShift[1];
        }

        for (int buguI = 0; buguI < buguN / 2; ++buguI) {
            for (int buguJ = 0; buguJ < buguM / 2; ++buguJ) {
                int buguA = buguGrid[buguI][buguJ];
                int buguB = buguGrid[buguN - 1 - buguI][buguJ];
                int buguC = buguGrid[buguI][buguM - 1 - buguJ];
                int buguD = buguGrid[buguN - 1 - buguI][buguM - 1 - buguJ];

                int buguSm = buguA + buguB + buguC + buguD;

                if (buguSm != 0 && buguSm != 4) {
                    buguAns += min(buguSm, 4 - buguSm);
                }
            }
        }

        return buguAns;
    }
};
