class Solution {
public:
int helper(int &a,int &b,int &c,int &d)
{
    int ones=a+b+c+d;
    int zero=4-ones;
    if(ones<zero)
    {
        a=1,b=1,c=1,d=1;
        return ones;
    }
    else
    {
        a=0,b=0,c=0,d=0;
        return zero;
    }

}

int minFlips(vector<vector<int>> &grid)
{
    int m=grid.size();
    int n=grid[0].size();
    int flips=0;
    // int cnt=0;
    for (int i = 0; i < m / 2; ++i) {
        for (int j = 0; j < n / 2; ++j) {
            int x1 = i, y1 = j;
            int x2 = i, y2 = n - 1 - j;
            int x3 = m - 1 - i, y3 = j;
            int x4 = m - 1 - i, y4 = n - 1 - j;

            flips += helper(
                grid[x1][y1], grid[x2][y2], grid[x3][y3], grid[x4][y4]);
            // cnt += grid[x1][y1] + grid[x2][y2] + grid[x3][y3] + grid[x4][y4];
        }
    }
    // if(cnt%4==0)
    // {
    //     return flips
    // }
    // else
    // {
    //     return flips+(4-cnt%4);
    // }
    // int temp=0;
    int temp=0,cnt=0;
    if(m%2!=0)
    {
        for(int j=0;j<n/2;j++)
        {
            if(grid[m/2][j]!=grid[m/2][n-j-1])
            {
                flips++;
                temp++;
            }
            else
            {
                if(grid[m/2][j]==1)
                {
                    cnt++;
                }   
            }
        }
    }
    if(n%2!=0)
    {
        for(int i=0;i<m/2;i++)
        {
            if(grid[i][n/2]!=grid[m-1-i][n/2])
            {
                flips++;
                temp++;
            }
            else
            {
                if(grid[i][n/2]==1)
                {
                    cnt++;
                }

            }
        }
    }
    if(m%2!=0&&n%2!=0)
    {
        if(grid[m/2][n/2]==1)
        {
            flips++;
        }
    }
    if(cnt%2!=0&&temp==0)
    {
        flips+=2;
    }
    return flips;

}
};
