class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size(), val = 0;

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                vector<int> pk = {
                    g[i][j],
                    g[n - i - 1][j],
                    g[i][m - j - 1],
                    g[n - i - 1][m - j - 1]
                };

                int vn = count(pk.begin(), pk.end(), 1);
                val += min(vn, 4 - vn);
            }
        }

        int mk = 0, cnt = 0;


        if (n % 2 != 0) {
            for (int i = 0; i < m / 2; ++i) {
                if (g[n / 2][i] != g[n / 2][m - i - 1]) {
                    val += 1;
                    mk += 1;
                } else {
                    if (g[n / 2][i] == 1) 
                        cnt += 1;

                }
            }
        }


        if (m % 2 != 0) {
            for (int k = 0; k < n / 2; ++k) {
                if (g[k][m / 2] != g[n - k - 1][m / 2]) {
                    val+= 1;
                    mk += 1;
                } else {
                    if (g[k][m / 2] == 1) 
                        cnt += 1;

                }
            }
        }


        if ((n % 2) != 0 && (m % 2 != 0) && (g[n / 2][m / 2]) == 1) {
            val += 1;
        }

        if (mk == 0 && (cnt % 2 != 0)) {
            val += 2;
        }

        return val;
    }
};
