class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length, m = grid[0].length, ans = 0;
        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int[] freq = new int[2];
                freq[grid[i][j]]++;
                freq[grid[i][m - j - 1]]++;
                freq[grid[n - i - 1][j]]++;
                freq[grid[n - i - 1][m - j - 1]]++;

                ans += 4 - Math.max(freq[0], freq[1]);
            }
        }

        if ((n & 1) == 1 && (m & 1) == 1 && grid[n >> 1][m >> 1] == 1) {
            ans++;
            grid[n >> 1][m >> 1] = 0;
        }

        int countRow = 0, countPair1 = 0, countCol = 0;
        if ((n & 1) == 1) {
            for (int j = 0; j < m / 2; j++) {
                if (grid[n >> 1][j] != grid[n >> 1][m - j - 1]) {
                    ans++;
                    countRow++;
                } else
                    countPair1 += grid[n >> 1][j];
            }
        }
        if ((m & 1) == 1) {
            for (int i = 0; i < n / 2; i++) {
                if (grid[i][m >> 1] != grid[n - i - 1][m >> 1]) {
                    ans++;
                    countCol++;
                } else
                    countPair1 += grid[i][m >> 1];
            }
        }

        if ((countPair1 & 1) == 1 && countRow == 0 && countCol == 0)
            ans += 2;
        return ans;
    }
}
