class Solution
{
public:
    int minFlips(vector<vector<int>> &grid)
    {
        int n = grid.size();
        int m = grid[0].size();

        int ans = 0;
        for (int i = 0; i < (m / 2); i++)
        {
            int l1 = i;
            int l2 = m - i - 1;
            int r1 = 0;
            int r2 = n - 1;
            // cout<<r1<<" "<<l1<<endl;
            // cout<<r1<<" "<<l2<<endl;
            // cout<<r2<<" "<<l1<<endl;
            // cout<<r2<<" "<<l2<<endl;

            // cout<<grid[l1][r1]<<" "<<grid[l1][r2]<<" "<<grid[l2][r1]<<" "<<grid[l2][r2]<<endl;
            while (r1 < r2)
            {
                int cnt = grid[r1][l1] + grid[r1][l2] + grid[r2][l1] + grid[r2][l2];
                int cnt1 = 4 - cnt;
                ans += min(cnt, cnt1);
                r1++;
                r2--;
            }
        }
        // cout << ans << " ";
        if (n % 2 && m % 2)
        {
            int cnt00 = 0;
            int cnt11 = 0;
            int cnt01 = 0;
            int mid = m / 2;

            for (int i = 0; i < n / 2; i++)
            {
                if (grid[i][mid] == grid[n - i - 1][mid])
                {
                    if (grid[i][mid])
                        cnt11++;
                    else
                        cnt00++;
                }
                else
                {
                    cnt01++;
                }
            }
            mid = n / 2;
            for (int i = 0; i < m / 2; i++)
            {
                if (grid[mid][i] == grid[mid][m - i - 1])
                {
                    if (grid[mid][i])
                        cnt11++;
                    else
                        cnt00++;
                }
                else
                {
                    cnt01++;
                }
            }

            if (cnt11 % 2 == 0)
            {
                ans += cnt01;
            }
            else if (cnt01)
            {
                ans += cnt01;
            }
            else
            {
                ans += 2;
            }
        }
        else if (n % 2)
        {
            int cnt00 = 0;
            int cnt11 = 0;
            int cnt01 = 0;
            int mid = n / 2;

            for (int i = 0; i < m / 2; i++)
            {
                if (grid[mid][i] == grid[mid][m - i - 1])
                {
                    if (grid[mid][i])
                        cnt11++;
                    else
                        cnt00++;
                }
                else
                {
                    cnt01++;
                }
            }

            if (cnt11 % 2 == 0)
            {
                ans += cnt01;
            }
            else if (cnt01)
            {
                ans += cnt01;
            }
            else
            {
                ans += 2;
            }
        }
        else if (m % 2)
        {
            int cnt00 = 0;
            int cnt11 = 0;
            int cnt01 = 0;
            int mid = m / 2;

            for (int i = 0; i < n / 2; i++)
            {
                if (grid[i][mid] == grid[n - i - 1][mid])
                {
                    if (grid[i][mid])
                        cnt11++;
                    else
                        cnt00++;
                }
                else
                {
                    cnt01++;
                }
            }

            if (cnt11 % 2 == 0)
            {
                ans += cnt01;
            }
            else if (cnt01)
            {
                ans += cnt01;
            }
            else
            {
                ans += 2;
            }
        }

        if (n % 2 && m % 2)
        {
            if (grid[(n / 2)][(m / 2)])
                ans++;
        }

        return ans;
    }
};


