class Solution {
public:

    int pal(vector<int>&arr){
        int n = arr.size();
        int i =0 ; int j =n-1 ;
        int cnt =0 ;
        while(i<=j){
            if(arr[i]!=arr[j]){
               cnt++;
            }
             i++;j--;
        }

        return cnt ;
    }
    int minFlips(vector<vector<int>>& grid) {



        int n = grid.size();
        int m = grid[0].size();

        int res = 0;

        if(n%2==1 && m%2==1){
            if(grid[n/2][m/2]){
                res+=1;
            }
        }

        vector<int>temp(3,0);

        if(n%2){
            int x =0 ; int y = m-1 ;
            while(x<y){
                int tt = grid[n/2][x] + grid[n/2][y] ;
                temp[tt] +=1;
                x+=1;
                y-=1;
            }
        }

        if(m%2){

            int x =0 ; int y = n-1 ;
            while(x<y){
                int tt = grid[x][m/2] + grid[y][m/2] ;
                temp[tt] +=1;
                x+=1;
                y-=1;
            }

        }

        if(temp[2]%2){
            if(temp[1]>0){
                res+=temp[1];
            }
            else{
                res+=2;
            }
        }
        else{
            res+=temp[1];
        }


        for(int i =0 ; i<n/2; ++i){
            for( int j =0 ; j<m/2; ++j){
                int a= grid[i][j];
                int b = grid[n-i-1][j];
                int c = grid[i][m-1-j];
                int d = grid[n-1-i][m-1-j];

                int ans = a+b+c+d;

                if(ans!=0 && ans!=4){
                    res+=(min(ans,4-ans));
                }
            }
        }


        return res ;
























    }
};
