class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size(), ret = 0;
        for (int u=0, d=n-1; u<d; u++, d--) {
            for (int l=0, r=m-1; l<r; l++, r--) {
                int sum = g[u][l] + g[u][r] + g[d][l] + g[d][r];
                ret += min(sum, 4-sum);
            }
        }

        int cnt1 = 0, change = 0;
        if (n&1) {
            int midn = n/2;
            for (int l=0, r=m-1; l<r; l++, r--) {
                int sum = g[midn][l] + g[midn][r];
                ret += min(sum, 2-sum);
                cnt1 += (sum == 0 ? 0 : 2);
                change += 2 * min(sum, 2-sum);
            }
        }
        if (m&1) {
            int midm = m/2;
            for (int u=0, d=n-1; u<d; u++, d--) {
                int sum = g[u][midm] + g[d][midm];
                ret += min(sum, 2-sum);
                cnt1 += (sum == 0 ? 0 : 2);
                change += 2 * min(sum, 2-sum);
            }
        }
        // cout << cnt1 << ' ' << change << '\n';
        if (cnt1 % 4 > change) ret += 2;

        if ((n&1) && (m&1) && (g[n/2][m/2])) ret++;

        return ret;
    }
};
