class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();

        int ans = 0;
        int center_num = 0;
        int center1 = 0;
        for (int il = 0, ir = m-1; il <= ir; il++, ir--) {
            for (int jl = 0, jr = n-1; jl <= jr; jl++, jr--) {
                int dp[2] = {0};
                if (il == ir && jl == jr) {
                    if (grid[il][jl] == 1) {
                        ans++;
                    }
                    continue;
                }
                if (il == ir) {
                    if (grid[il][jl] != grid[il][jr]) {
                        center_num++;
                    } else if (grid[il][jl] == 1) {
                        center1 ++;
                    }
                    continue;
                }
                if (jl == jr) {
                    if (grid[il][jl] != grid[ir][jl]) {
                        center_num++;
                    } else if (grid[il][jl] == 1) {
                        center1 ++;
                    }
                    continue;
                }
                dp[grid[il][jl]]++;
                dp[grid[il][jr]]++;
                dp[grid[ir][jl]]++;
                dp[grid[ir][jr]]++;
                ans += min(dp[0], dp[1]);
            }
        }


        if (center1 % 2 != 0 && center_num == 0) {
            ans += 2;
        }
        return ans + center_num;
    }
};
