class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int flips = 0;
        // Squares
        for(int i = 0; i < grid.size() / 2; i++) {
            for(int j = 0; j < grid[i].size() / 2; j++) {
                int temp = grid[i][j] + grid[grid.size() - 1 - i][j] + grid[i][grid[i].size() - 1 - j] + grid[grid.size() - 1 - i][grid[i].size() - 1 - j];
                flips += min(4 - temp, temp);
            }
        }
        // cout << flips << endl;
        //Center
        if(grid.size() % 2 == 1 && grid[0].size() % 2 == 1) {
            flips += grid[grid.size()/2][grid[0].size()/2];
            grid[grid.size()/2][grid[0].size()/2] = 0;
        }
        // cout << flips << endl;
        // Off-centers
        int temp = 0;
        bool potential = false;
        if(grid[0].size() % 2 == 1) {
            for(int i = 0; i < grid.size()/2; i++) {
                if(grid[i][grid[0].size()/2] != grid[grid.size() - 1 - i][grid[0].size()/2]) {
                    potential = true;
                    flips++;
                } else if (grid[i][grid[0].size()/2] == 1) {
                    temp += 2;
                }
            }
        }
        // cout << flips << endl;
        if(grid.size() % 2 == 1) {
            for(int i = 0; i < grid[0].size()/2; i++) {
                if(grid[grid.size()/2][i] != grid[grid.size()/2][grid[0].size() - 1 - i]) {
                    potential = true;
                    flips++;
                } else if (grid[grid.size()/2][i] == 1) {
                    temp += 2;
                }
            }
        }
        if(temp % 4 != 0) {
                if(temp % 2 == 0 && !potential) {
                    flips += 2;
                }
            }
        // cout << flips << endl;
        return flips;
    }
};
