class Solution {
    public int minFlips(int[][] grid) {
        int mn=grid.length;
        int mm=grid[0].length;

        int ans=0;

        if((mn%2==1) && (mm%2==1)){
            if(grid[mn/2][mm/2]==1)ans+=1;
        }
        int [] temp=new int[3];
        if(mn%2==1){
            int l=0,r=mm-1;
            while(l<r){
                int ss=grid[mn/2][l] + grid[mn/2][r];
                temp[ss] += 1;
                l++;
                r--;
            }
        }
        if(mm%2==1){
            int l=0,r=mn-1;
            while(l<r){
                int ss=grid[l][mm/2] + grid[r][mm/2];
                temp[ss] += 1;
                l++;
                r--;
            }
        }

        if(temp[2]%2==1){
            if(temp[1]>0){
                ans += temp[1];
            }
            else{
                ans += 2;
            }
        }
        else{
            ans += temp[1];
        }

        for(int i=0;i<mn/2;i++){
            for(int j=0;j<mm/2;j++){
                int a1=grid[i][j];
                int a2=grid[mn-i-1][j];
                int a3=grid[i][mm-j-1];
                int a4=grid[mn-1-i][mm-1-j];

                int ss=a1+a2+a3+a4;
                if(ss!=0 && ss!=4){
                    ans += Math.min(ss,4-ss);
                }
            }
        }
        return ans;
}
}
