class Solution {
public:

bool check(int n,int m){
    if(n%2 && m%2)return 1;
    return 0;
}
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        vector<int> v(10,0);

        int cnt = 0;
        if(check(n,m)){
            if(grid[n/2][m/2])cnt++;
        }

        int cnt1=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j]==1)cnt1++;
            }
        }

bool f1=m%2;

if(f1){
            int s=0;
            int e = n-1;
            while(s<e){
                int index = grid[s][m/2]+grid[e][m/2];
                v[index]++;
                s++;
                e--;
            }
        }

bool f2=n%2;
        if(f2){
            int s = 0;
            int e= m-1;
            while(s<e){
                int index = grid[n/2][s]+grid[n/2][e];
                v[index]+=1;
                s++;
                e--;
            }
        }

bool f3=v[2]%2;

        if(f3){
            if(v[1]>0){
                cnt+=v[1];
            }
            else cnt+=2;
        }
        else{
            cnt+=v[1];
        }


        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int total=grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1];
                if(total!=0 && total!=4){
                    cnt+=min(total,4-total);
                }
            }
        }
return cnt;





    }
};
