class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int total_flips_needed = 0;
        int potential_flips_needed = 0;
        int number_of_rows = grid.size();
        int number_of_columns = grid[0].size();
        int count_of_ones_in_middle = 0;
        for (int row_index = 0; row_index < number_of_rows / 2; ++row_index) {
            for (int column_index = 0; column_index < number_of_columns / 2; ++column_index) {
                vector<int> sub_grid_elements = {
                    grid[row_index][column_index],
                    grid[number_of_rows - row_index - 1][column_index],
                    grid[row_index][number_of_columns - column_index - 1],
                    grid[number_of_rows - row_index - 1][number_of_columns - column_index - 1]
                };

                int number_of_ones_in_sub_grid = accumulate(sub_grid_elements.begin(), sub_grid_elements.end(), 0);
                total_flips_needed += min(number_of_ones_in_sub_grid, 4 - number_of_ones_in_sub_grid);
            }
        }
        if (number_of_rows % 2 != 0) {
            int middle_row_index = number_of_rows / 2;
            for (int column_index = 0; column_index < number_of_columns / 2; ++column_index) {
                if (grid[middle_row_index][column_index] != grid[middle_row_index][number_of_columns - column_index - 1]) {
                    total_flips_needed += 1;
                    potential_flips_needed += 1;
                } else if (grid[middle_row_index][column_index] == 1) {
                    count_of_ones_in_middle += 1;
                }
            }
        }

        if (number_of_columns % 2 != 0) {
            int middle_column_index = number_of_columns / 2;
            for (int row_index = 0; row_index < number_of_rows / 2; ++row_index) {
                if (grid[row_index][middle_column_index] != grid[number_of_rows - row_index - 1][middle_column_index]) {
                    total_flips_needed += 1;
                    potential_flips_needed += 1;
                } else if (grid[row_index][middle_column_index] == 1) {
                    count_of_ones_in_middle += 1;
                }
            }
        }


        if (potential_flips_needed == 0 && count_of_ones_in_middle % 2 != 0) {
            total_flips_needed += 2;
        }
        if (number_of_rows % 2 != 0 && number_of_columns % 2 != 0 && grid[number_of_rows / 2][number_of_columns / 2] == 1) {
            total_flips_needed += 1;
        }


        return total_flips_needed;
    }
};
