class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0, ans1 = 0, n = grid.size(), m= grid[0].size();
        int i = 0, i1 = n - 1, cn = 0, cn1 = 0;
        while(i < i1){
            for(int j = 0, j1 = m - 1; j < j1; j++, j1--){
                cn += 4;
                int c = grid[i][j], d = grid[i][j1], e = grid[i1][j], f = grid[i1][j1];
                int cnt = 0, cnt1 = 0;
                if(c == 0) cnt++;
                else cnt1++;
                if(d == 0) cnt++;
                else cnt1++;
                if(e == 0) cnt++;
                else cnt1++;
                if(f == 0) cnt++;
                else cnt1++;
                ans += min(cnt, cnt1);
                grid[i][j] = grid[i][j1] = grid[i1][j] = grid[i1][j1] = 0;
            }
            i++;i1--;
        }
        if(n % 2){
            for(int i = 0, j = m - 1; i < j; i++, j--){
                if(grid[n / 2][i] == grid[n / 2][j]){
                    if(grid[n / 2][i] == 1){
                        cn+= 2;
                    }
                }
                else cn1+= 2, ans++;
            }
        }
        if(m % 2){
            for(int i = 0, j = n - 1; i < j; i++, j--){
                if(grid[i][m / 2] == grid[j][m/2]){
                    if(grid[i][m / 2] == 1) cn+= 2;
                }
                else cn1+= 2, ans++;;
            }
        }
        if((n % 2) && (m % 2)){
            n /= 2; 
            m /= 2;
            if(grid[n][m] == 1) ans++;
        } 
        if(cn % 4 == 0){
            return ans;
        }
        else{
            int c = 4 - (cn % 4);
            if(cn1 >= c) return ans;
            else{
                return ans + (cn % 4);
            }
        }
    }
};
