class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n,m = len(grid),len(grid[0])
        res = 0

        # 先不看中间的行和列
        for i in range(n//2):
            for j in range(m//2):
                one,zero = 0,0
                for num in  [grid[i][j],grid[i][m-j-1],grid[n-i-1][j],grid[n-i-1][m-j-1]]:
                    if num:
                        one += 1
                    else:
                        zero += 1
                res += min(one,zero)

        if n&1 and m&1 and grid[n//2][m//2]:
            # 最中间必须变为0
            res += 1

        t00,t11,t01 = 0,0,0
        if n&1:
            i = n // 2
            for j in range(m//2):
                tmp = grid[i][j]+grid[i][m-j-1]
                if not tmp:
                    t00 += 1
                elif tmp == 2:
                    t11 += 1
                else:
                    res += 1
                    t01 += 1

        if m&1:
            j = m // 2
            for i in range(n//2):
                tmp = grid[i][j] + grid[n-i-1][j]
                if not tmp:
                    t00 += 1
                elif tmp == 2:
                    t11 += 1
                else:
                    res += 1
                    t01 += 1

        # 1个数被4整除
        if t11 % 2 == 0:
            return res

        # 还差 2 个 1，把1个t01 变成11
        if t01 >= 1:
            return res
        else:
            return res + 2
