class Solution {
public:
    int solve(int i, int j, const vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        vector<int> nums = {
            grid[i][j], grid[n - i - 1][j], grid[i][m - j - 1], grid[n - i - 1][m - j - 1]
        };

        int numOne = accumulate(nums.begin(), nums.end(), 0);
        return min(numOne, 4 - numOne);
    }

    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int cnt = 0;

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                cnt += solve(i, j, grid);
            }
        }

        int f = 0, sum = 0;

        // Handle middle row if the number of rows is odd
        if (n % 2 != 0) {
            int midr = n / 2;

            for (int j = 0; j < m / 2; ++j) {
                switch (grid[midr][j] != grid[midr][m - j - 1]) {
                    case true:
                        ++cnt;
                        ++f;
                        break;
                    case false:
                        if (grid[midr][j] == 1) {
                            ++sum;
                        }
                        break;
                }
            }
        }

        // Handle middle column if the number of columns is odd
        if (m % 2 != 0) {
            int midc = m / 2;
            for (int i = 0; i < n / 2; ++i) {
                switch (grid[i][midc] != grid[n - i - 1][midc]) {
                    case true:
                        ++cnt;
                        ++f;
                        break;
                    case false:
                        if (grid[i][midc] == 1) {
                            ++sum;
                        }
                        break;
                }
            }
        }

        if (n % 2 != 0 && m % 2 != 0 && grid[n / 2][m / 2] == 1) {
            ++cnt;
        }

        if (f == 0 && sum % 2 != 0) {
            cnt += 2;
        }

        return cnt;
    }
};
