class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        vector<int> arr(3, 0);
        int mini=0;
        if (m%2==1 && n%2==1) 
        {
            if (grid[n/2][m/2]==1) 
            mini++;
        }
        if (n%2==1) 
        {
            int f=0;
            int e=m-1;
            while (f<e) 
            {
                int curr=grid[n/2][f]+grid[n/2][e];
                arr[curr]++;   
                f++;
                e--;
            }
        }


        if (m%2==1) 
        {
            int f=0;
            int e=n-1;
            while (f<e) 
            {
                int curr=grid[f][m/2]+grid[e][m/2];
                arr[curr]++;
                f++;
                e--;   
            }
        }


        if (arr[2]%2==1) 
        {
            if (arr[1]>0) 
            mini+=arr[1];
            else
            mini+=2;
        }
        else
        mini+=arr[1];
        for (int i=0;i<n/2;i++) 
        {
            for (int j=0;j<m/2;j++) 
            {
                int curr=grid[i][j]+grid[n-1-i][j]+grid[i][m-1-j]+grid[n-1-i][m-1-j];
                if (curr!=0 && curr!=4) 
                mini+=min(curr,4-curr);
            }
        }

        return mini;
    }
};
