class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        if((n&1) && (m&1)){
            if(grid[n/2][m/2]) ans++;
        }
        vector<int> move(3, 0);
        if(n&1){
            int x = 0, y = m - 1;
            while(x < y){
                int id = grid[n/2][x] + grid[n/2][y];
                move[id]++;
                x++;
                y--;
            }
        }
        if(m&1){
            int x = 0, y = n - 1;
            while(x < y){
                int id = grid[x][m/2] + grid[y][m/2];
                move[id]++;
                x++;
                y--;
            }
        }
        if(move[2]&1){
            if(move[1]) ans += move[1];
            else ans += 2;
        } 
        else ans += move[1];
        for(int i = 0;i < n/2;i++){
            int k1 = n - i - 1;
            for(int j = 0;j < m/2;j++){
                int k2 = m - j - 1;
                int tot = grid[i][j] + grid[k1][j] + grid[i][k2] + grid[k1][k2];
                if (tot && tot != 4) {
                    ans += min(tot, 4 - tot);
                }
            }
        }
        return ans;
    }
};
