class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int total=0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                total+=grid[i][j];
            }
        }
        int ans=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int curr=grid[i][j]+grid[m-1-i][j]+grid[i][n-1-j]+grid[m-1-i][n-1-j];
                total-=curr;
                if(curr==0 ||curr==4)continue;
                else{
                    ans+=min(4-curr,curr);
                }
            }
        }
        if(n%2 && m%2 && grid[m/2][n/2]){
            ans++;
        }
        vector<int>arr(3,0);
        if(m%2==1){
            int i=0,j=n-1;
            while(i<j){
                arr[grid[m/2][i]+grid[m/2][j]]++;
                i++;j--;
            }
        }
        if(n%2==1){
            int i=0,j=m-1;
            while(i<j){
                arr[grid[i][n/2]+grid[j][n/2]]++;
                i++;j--;
            }
        }
        if(arr[2]%2==1){
            if(arr[1]>0){
                ans+=arr[1];
            }else{
                ans+=2;
            }
        }else{
            ans+=arr[1];
        }
        return ans;
    }
};
