class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        int ret = 0;
        if (n % 2 == 0 && m % 2 == 0) {
            for (int i = 0; i < n / 2; i++) {
                for (int j = 0; j < m / 2; j++) {
                    int u = g[i][j] + g[i][m - 1 - j] + g[n - 1 - i][j] +
                            g[n - 1 - i][m - 1 - j];
                    ret += min(u, 4 - u);
                }
            }
        } else if (n % 2 == 0 && (m & 1)) {
            for (int i = 0; i < n / 2; i++) {
                for (int j = 0; j < m / 2; j++) {
                    int u = g[i][j] + g[i][m - 1 - j] + g[n - 1 - i][j] +
                            g[n - 1 - i][m - 1 - j];
                    ret += min(u, 4 - u);
                }
            }

            int c = 0, f = 0;
            for(int i = 0 ; i < n / 2; i ++) {
                int u = g[i][m / 2 ] + g[n - 1 - i][m / 2];
                if(u == 2) c++;
                if(u == 1) { f = 1;
                   ret++;
                }
            }
            if(c & 1 && !f) ret += 2;
        } else if (m % 2 == 0 && (n & 1)) {
            for (int i = 0; i < n / 2; i++) {
                for (int j = 0; j < m / 2; j++) {
                    int u = g[i][j] + g[i][m - 1 - j] + g[n - 1 - i][j] +
                            g[n - 1 - i][m - 1 - j];
                    ret += min(u, 4 - u);
                }
            }
            int c = 0, f = 0;
            for(int i = 0 ; i < m / 2; i ++) {
                int u = g[n / 2 ][i] + g[n / 2 ][m - 1 - i];
                if(u == 2) c++;
                if(u == 1) {
                    f = 1;
                    //if(c & 1) c++, ret++;
                     ret++;
                }
            }
            if(c & 1 && !f) ret += 2;
        } else {
            for (int i = 0; i < n / 2; i++) {
                for (int j = 0; j < m / 2; j++) {
                    int u = g[i][j] + g[i][m - 1 - j] + g[n - 1 - i][j] +
                            g[n - 1 - i][m - 1 - j];
                    ret += min(u, 4 - u);
                }
            }
            int c = 0, f = 0;
            for(int i = 0; i < n / 2; i ++) {
                int u = g[i][m / 2 ] + g[n - 1 - i][m / 2 ];
                if(u == 2) c++;
                else if(u == 1) {
                    f = 1;
                   // if(c & 1) c++, ret++;
                     ret++;
                }

            }
            for(int i = 0 ; i < m / 2; i ++) {
                int u = g[n / 2 ][i] + g[n / 2 ][m - 1 - i];
                if(u == 2) c++;
                else if(u == 1) {
                    //if(c & 1) c++, ret++;
                    f = 1;
                    ret++;
                }
            }
            if(g[n / 2 ][m / 2 ]) ret++;
            if(c & 1 && !f) ret += 2;
        }
        return ret;
    }
};
