class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int ans = 0;

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                vector<int> elements = {
                    grid[i][j],
                    grid[n - i - 1][j],
                    grid[i][m - j - 1],
                    grid[n - i - 1][m - j - 1]
                };

                int ones = count(elements.begin(), elements.end(), 1);

                ans += min(ones, 4 - ones);
            }
        }

        int possible = 0;
        int count = 0;

        if (n % 2 != 0) {
            for (int j = 0; j < m / 2; ++j) {
                if (grid[n / 2][j] != grid[n / 2][m - j - 1]) {
                    ans += 1;
                    possible += 1;
                } else {
                    if (grid[n / 2][j] == 1) {
                        count += 1;
                    }
                }
            }
        }

        if (m&1) {
            for (int i = 0; i < n / 2; i+=1) {
                if (grid[i][m / 2] != grid[n - i - 1][m / 2]) {
                    ans += 1;
                    possible += 1;
                } else {
                    if (grid[i][m / 2] == 1) {
                        count += 1;
                    }
                }
            }
        }

        if ((n&1) && m % 2 != 0 && grid[n / 2][m / 2] == 1) {
            ans ++;
        }

        if (possible == 0 && count % 2 != 0) {
            ans += 2;
        }

        return ans;
    }
};
