class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        # 奇数行和奇数列需要额外处理，分别统计需要反转的1的数量和无需反转的1，
        m = len(grid)
        n = len(grid[0])
        res = 0
        # 遍历方式有问题，没有遍历整个网格
        for i in range(m//2):
            for j in range(n//2):
            # 统计四个角的情况，最多需要两次,最少需要1或0
                temp =  grid[i][j]+grid[m-1-i][j]+grid[i][n-1-j]+grid[m-1-i][n-1-j]
                if(temp==1 or temp==3):
                    res+=1
                if(temp==2):
                    res+=2
        # m或n为奇数的情况
        need_flip_cnt  = 0
        no_flip_cnt = 0
        if(m%2==1):
            # grid[m//2][j]
            for j in range(n//2):
                if(grid[m//2][j]!=grid[m//2][n-1-j]):
                    need_flip_cnt+=1
                elif(grid[m//2][j]==1):
                    no_flip_cnt+=1
        if(n%2==1):
            # grid[m//2][j]
            for i in range(m//2):
                if(grid[i][n//2]!=grid[m-1-i][n//2]):
                    need_flip_cnt+=1
                elif(grid[i][n//2]==1):
                    no_flip_cnt+=1
        res+=need_flip_cnt
        if(no_flip_cnt%2==1 and need_flip_cnt<1):
            res+=2
        if(m%2==1 and n%2==1 and grid[m//2][n//2]==1):
            res+=1
        return res
