class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int output = 0;

        for (int i=0;i<n/2;i++) {
            for (int j=0;j<m/2;j++) {

                int count = grid[i][j] + grid[n-i-1][j] + grid[n-i-1][m-j-1] + grid[i][m-j-1];

                if (count <= 2) output += count;
                else output += 4 - count;

                grid[i][j] = grid[n-i-1][j] = grid[n-i-1][m-j-1] = grid[i][m-j-1] = -1;
            }
        }

        int onepairs = 0;
        int pos = 0;
        if (n % 2 == 1) {
            for (int j=0;j<m/2;j++) {
                if (grid[n/2][j] != grid[n/2][m-j-1]) pos++;
                else if (grid[n/2][j] == 1) onepairs++;
            }
        }

        if (m % 2 == 1) {
            for (int i=0;i<n/2;i++) {
                if (grid[i][m/2] != grid[n-i-1][m/2]) pos++;
                else if (grid[i][m/2] == 1) onepairs++;
            }
        }

        if (m % 2 == 1 && n % 2 == 1) {
            if (grid[n/2][m/2] == 1) {
                output++;
            }
        } 

        if (onepairs % 2 == 0) output += pos;
        else if (onepairs % 2 <= pos) output += pos;
        else output += pos + (onepairs % 2 - pos) * 2;

        return output;



    }
};
