#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int numRows = grid.size();
        int numCols = grid[0].size();
        int totalFlips = 0;

        for (int row = 0; row < numRows / 2; ++row) {
            for (int col = 0; col < numCols / 2; ++col) {
                int topLeft = grid[row][col];
                int bottomLeft = grid[numRows - row - 1][col];
                int topRight = grid[row][numCols - col - 1];
                int bottomRight = grid[numRows - row - 1][numCols - col - 1];

                int numOnes = topLeft + bottomLeft + topRight + bottomRight;
                totalFlips += min(numOnes, 4 - numOnes);
            }
        }

        int middleRowFlips = 0;
        int middleRowCount = 0;

        if (numRows % 2 != 0) {
            int midRow = numRows / 2;
            for (int col = 0; col < numCols / 2; ++col) {
                if (grid[midRow][col] != grid[midRow][numCols - col - 1]) {
                    ++totalFlips;
                    ++middleRowFlips;
                } else if (grid[midRow][col] == 1) {
                    ++middleRowCount;
                }
            }
        }

        int middleColFlips = 0;
        int middleColCount = 0;

        if (numCols % 2 != 0) {
            int midCol = numCols / 2;
            for (int row = 0; row < numRows / 2; ++row) {
                if (grid[row][midCol] != grid[numRows - row - 1][midCol]) {
                    ++totalFlips;
                    ++middleColFlips;
                } else if (grid[row][midCol] == 1) {
                    ++middleColCount;
                }
            }
        }

        if (numRows % 2 != 0 && numCols % 2 != 0 && grid[numRows / 2][numCols / 2] == 1) {
            ++totalFlips;
        }

        if (middleRowFlips == 0 && middleColFlips == 0 && (middleRowCount + middleColCount) % 2 != 0) {
            totalFlips += 2;
        }

        return totalFlips;
    }
};
