class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int cnt1=0,cnt0=0,sum1=0,sum2=0;
        for(int i=0;i<grid.size();i++){
            for(int j=0;j<grid[i].size();j++){
                //printf("i:%d j:%d\n",i,j);
                if(grid[i][j]==1)cnt1++;
                else cnt0++;
            }
        }
        int ans=0;
        for(int i=0;i<grid.size()/2;i++){
            for(int j=0;j<grid[i].size()/2;j++){
                int ans0=0,ans1=0;
                if(grid[i][j]==0)ans0++;
                else ans1++;
                if(grid[i][grid[i].size()-j-1]==0)ans0++;
                else ans1++;
                if(grid[grid.size()-i-1][j]==0)ans0++;
                else ans1++;
                if(grid[grid.size()-i-1][grid[i].size()-j-1]==0)ans0++;
                else ans1++;   
                ans+=min(ans0,ans1);            
            }
        }
        int cnt=0,tmp=0;
        if(grid.size()&1){
            int pos=grid.size()/2;
            for(int j=0;j<grid[0].size()/2;j++){
                if(grid[pos][j]!=grid[pos][grid[0].size()-j-1])tmp++;
            }
            for(int j=0;j<grid[0].size();j++){
                if(grid[pos][j]==1)cnt++;
            }            
        }
        if(grid[0].size()&1){
            int pos=grid[0].size()/2;
            for(int j=0;j<grid.size()/2;j++){
                if(grid[j][pos]!=grid[grid.size()-j-1][pos])tmp++;
            }
            for(int j=0;j<grid.size();j++){
                if(grid[j][pos]==1)cnt++;
            }  
            if(grid.size()&1){
                if(grid[grid.size()/2][pos]==1)cnt--;
            }          
        }
        if(((cnt&1)^(tmp&1))==0){
            if(cnt%4==2&&!tmp)tmp=2;
        }
        else{
            if(cnt%4==3&&!tmp)tmp=3;
            else tmp++;
        }
        return tmp+ans;
    }
};
