class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size() , m = a[0].size();
        vector<vector<int>> vis(n, vector<int> (m , 1));
        int ans = 0, cnt1=0 , change = 0;
        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                int c0 = 0 , c1 = 0;
                if(vis[i][j]){

                    int i2 = i , j2 = m-j-1;
                    int i3 = n-i-1 , j3 = j;
                    int i4 = n-i-1 , j4 = m-j-1;
                    if(a[i][j]) c1++;
                    else c0++;
                    vis[i][j] = 0;

                    if(vis[i2][j2]){
                        if(a[i2][j2]) c1++;
                        else c0++;
                        vis[i2][j2] = 0;
                    }

                    if(vis[i3][j3]){
                        if(a[i3][j3]) c1++;
                        else c0++;
                        vis[i3][j3] = 0;
                    }

                    if(vis[i4][j4]){
                        if(a[i4][j4]) c1++;
                        else c0++;
                        vis[i4][j4] = 0;
                    }

                    ans += min(c1,c0);
                }
            }
        }
        // cout<<ans<<endl;
        int check = 0;
        if(m%2){
            for(int i=0; i<n/2; i++){
                if(a[i][m/2] == a[n-i-1][m/2] and a[i][m/2] == 1) cnt1+=2;
                else if(a[i][m/2] != a[n-i-1][m/2]) ans++ , check = 1;
            }
        }
        if(n%2){
            for(int j=0; j<m/2; j++){
                if(a[n/2][j] == a[n/2][m-j-1] and a[n/2][j] == 1) cnt1+=2;
                else if(a[n/2][m-j-1] != a[n/2][j]) ans++ ,check = 1;
            }
        }
        if((n*m)%2 == 1  and a[n/2][m/2] == 1) cnt1++;

        if(cnt1 % 4 == 1){
            return ans+1;
        }else if(cnt1%4 == 2){
            if(check) return ans;
            else return ans+2;
        }else if(cnt1%4 == 3){
            if(check) return ans+1;
            else return ans+3;
        }else{
            return ans;
        }

    }
};
