class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int ans = 0;
        int n = a.size();
        int m = a[0].size();
        int one = 0;
        int pos=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int ct=0;
                if(a[i][j]) ct++;
                if(a[n-1-i][j]) ct++;
                if(a[i][m-1-j]) ct++;
                if(a[n-1-i][m-1-j]) ct++;
                if(ct==2) ans+=2;
                if(ct==3 || ct==1) ans+=1;

            }
        }
        if(m%2!=0){
            int mid = m / 2;
            for(int i=0;i<n/2;i++){
                int ct = 0;
                if(a[i][mid]) ct++;
                if(a[n-1-i][mid]) ct++;
                if(ct==1) ans++;
                if(ct==1) pos++;
                else if(ct==2) one++;
            }
        }

        if(n%2!=0){
            int mid = n / 2;
            for(int i=0;i<m/2;i++){
                int ct = 0;
                if(a[mid][i]) ct++;
                if(a[mid][m-1-i]) ct++;
                if(ct==1) ans++;
                if(ct==1) pos++;
                else if(ct==2) one++;
            }
        }
        if(one%2!=0){
            if(one==1 && pos==0) ans+=2;
            else if((one==1 && pos==1)) ans+=0;
            else if(pos==0) ans+=2;
        }

        if(n%2!=0 && m%2!=0 && a[n/2][m/2]) {
            ans++;
        }
        return ans;
    }
};
